        <input type="checkbox"
               class="input"
               v-model="lang"
               @input="changLang">
        <span class="slider"></span>
    </label>
</template>

<script>
export default {
    data: () => ({
        lang: false
    }),
    mounted() {
        this.$nextTick(() => {
            const _lang = localStorage.getItem('lang')
            _lang == 'en' ? this.lang = true : this.lang = false
        })
    },
    methods: {
        changLang(e) {
            this.$setLanguage(e.target.checked ? 'en' : 'vi')
            this.$router.go()
        }
    }
}
</script>

<style scoped>
.switch {
    font-size: 17px;
    position: relative;
    display: inline-block;
    width: 64px;
    height: 34px;
}

.switch input {
    opacity: 0;
    width: 0;
    height: 0;
}

.slider {
    position: absolute;
    cursor: pointer;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background-color: #7f7f7f;
    transition: .4s;
    border-radius: 30px;
}

.slider:before {
    position: absolute;
    content: "";
    height: 30px;
    width: 30px;
    border-radius: 20px;
    left: 2px;
    bottom: 2px;
    z-index: 2;
    background-color: #e8e8e8;
    transition: .4s;
}

.sun svg {
    position: absolute;
    top: 6px;
    left: 36px;
    z-index: 1;
    width: 24px;
    height: 24px;
}

.moon svg {
    fill: #73C0FC;
    position: absolute;
    top: 5px;
    left: 5px;
    z-index: 1;
    width: 24px;
    height: 24px;
}


.input:checked+.slider {
    background-color: #183153;
}

.input:focus+.slider {
    box-shadow: 0 0 1px #183153;
}

.input:checked+.slider:before {
    transform: translateX(30px);
}
</style>